mapper.select:
select
from xd_product_order where resources_id = #{resourceId,jdbcType=VARCHAR} ORDER BY create_time ASC
LIMIT #{page},#{pageSize}

SELECT COUNT(1)
FROM xd_product_order
WHERE resources_id = #{resourceId,jdbcType=VARCHAR}

select count(1)
from xd_product_order t1 LEFT JOIN xd_resources_base t2
on t1.resources_id=t2.resources_id AND t2.customer_phone=#{customerPhone,jdbcType=VARCHAR}
